import * as api from '../api/index';

//We are going to describe actions
const getPosts = () => async (dispatch) => {

    try {
        //we destrucure our data from the response 
        const { data } = await api.fetchPosts();
        const action = {
            type: 'FETCH_ALL',
            payload: data,
        };
        dispatch(action);
    } catch (err) {

    }

}

module.exports = getPosts;
